    @Override
    public boolean onCreateOptionsMenu(Menu menu) {

        getMenuInflater().inflate(R.menu.toolbar_menu,menu);

        MenuItem menuItem =menu.findItem(R.id.action_search);
        SearchView searchView=(SearchView) menuItem.getActionView();
        searchView.setOnQueryTextListener(this);

        return  true;
    }

    @Override
    public boolean onQueryTextSubmit(final String text_search) {
        if(array_contact.isEmpty()&&array_contact1.isEmpty()&&array_contact2.isEmpty()&&array_contact3.isEmpty()&&array_contact4.isEmpty()&&array_contact5.isEmpty())

        {
            Toast.makeText(this, "please Chose type filter befor search", Toast.LENGTH_LONG).show();
        }
        else {
            ArrayList<contact> newlist = new ArrayList<>();


            if (serch_type.equals("jewellery")) {
                for (contact contact : array_contact3) {
                    if (contact.getTxt_post().contains(text_search)) {
                        newlist.add(contact);

                        Log.i(TAG, "arraylist" + contact.getType_other());
                    }
                }
            } else if (serch_type.equals("money")) {

                for (contact contact : array_contact1) {
                    if (contact.getTxt_post().contains(text_search)) {
                        newlist.add(contact);
                        // newlist_img.add();
                        Log.i(TAG, "arraylist" + contact.getType_other());
                    }
                }

            } else if (serch_type.equals("others") || serch_type.equals("mobile")) {

                for (contact contact : array_contact2) {
                    if (contact.getTxt_post().contains(text_search)) {
                        newlist.add(contact);
                        // newlist_img.add();
                        Log.i(TAG, "arraylist" + contact.getType_other());
                    }
                }

            } else if (serch_type.equals("wallet")) {

                for (contact contact : array_contact) {
                    if (contact.getTxt_post().contains(text_search)) {
                        newlist.add(contact);
                        // newlist_img.add();
                        Log.i(TAG, "arraylist" + contact.getType_other());
                    }
                }

            } else if (serch_type.equals("officil_document")) {

                for (contact contact : array_contact4) {
                    if (contact.getTxt_post().contains(text_search)) {
                        newlist.add(contact);
                        // newlist_img.add();
                        Log.i(TAG, "arraylist" + contact.getType_other());
                    }
                }

            } else if (serch_type.equals("allpost")) {

                for (contact contact : array_contact5) {
                    if (contact.getTxt_post().contains(text_search)) {
                        newlist.add(contact);
                        // newlist_img.add();
                        Log.i(TAG, "arraylist" + contact.getType_other());
                    }
                }

            }


            if (newlist.isEmpty()) {
                Toast.makeText(this, "not found", Toast.LENGTH_LONG).show();

            }

            adapter.setfilter(newlist);


//        array_contact5.add(contact5);
//        adapter = new Adapter_search(getApplication(), url_Img5, array_contact5) ;
//        recyclerView.setAdapter(adapter);
//        adapter.notifyDataSetChanged();
        }
        return true;
    }



